mapper.insert:
INSERT INTO TB_CART
	(
	CART_CD
	,IT_CD
	,MB_ID
	,AMOUNT
	,OPT_CD
	)
VALUES
	(
	TB_CART_SEQ.NEXTVAL
	,#{itCd}
	,#{mbId}
	,#{amount}
	,#{optCd, jdbcType=INTEGER}
	)

mapper.update:
UPDATE 
	TB_CART
SET
	AMOUNT = #{amount}
WHERE
	MB_ID = #{mbId}
AND
	CART_CD = #{cartCd}

UPDATE 
	TB_CART
SET
	OPT_CD=#{optCd}
WHERE
	MB_ID=#{mbId}
AND
	CART_CD = #{cartCd}
