mapper.insert:
INSERT INTO TB_FILE_UPLOAD(
	IMG_CD
	, IMG_NM
	, SZ
	, DI
	, FL
) VALUES (
	TB_FILE_UPLOAD_SEQ.NEXTVAL
	, #{imgNm}
	, #{sz}
	, #{di}
	, #{fl}
)

	SELECT 
		TB_FILE_UPLOAD_SEQ.CURRVAL
	FROM DUAL

INSERT INTO TB_IT_INFO(
	IT_INFO_CD
	, IMG_NM
	, SZ
	, DI
	, FL
	, IT_CD
) VALUES (
	TB_IT_INFO_SEQ.NEXTVAL
	, #{imgNm}
	, #{sz}
	, #{di}
	, #{fl}
	, #{itCd}
)

mapper.update:
UPDATE
	TB_FILE_UPLOAD
SET
	IMG_NM = #{imgNm}
	, SZ = #{sz}
	, DI = #{di}
	, FL = #{fl}
WHERE
	IMG_CD = #{imgCd}

UPDATE
	TB_IT_INFO
SET
	IMG_NM = #{imgNm}
	, SZ = #{sz}
	, DI = #{di}
	, FL = #{fl}
WHERE IT_INFO_CD = #{itInfoCd}
