mapper.insert:
INSERT INTO TB_ITEM
	(
	IT_CD
	, IMG_CD
	, IT_NM
	, PRICE
	, CAT
	) 
VALUES 
	(
	TB_ITEM_SEQ.NEXTVAL
	, #{imgCd}
	, #{itNm}
	, #{price}
	, #{cat}
	)

	SELECT
		TB_ITEM_SEQ.CURRVAL
	FROM 
		DUAL

mapper.update:
UPDATE
	TB_ITEM
SET
	IT_NM = #{itNm}
	, PRICE = #{price}
	, CAT = #{cat}
WHERE
	IT_CD = #{itCd}

mapper.insert:
UPDATE
	TB_ITEM
SET
	OPT_CD = #{optCd}
WHERE
	IT_CD = #{itCd}
