mapper.insert:
INSERT INTO TB_MEMBER(
	MB_ID
	, PW
	, NM
	, POST
	, ADDR1
	, ADDR2
	, TEL1
	, TEL2
	, TEL3
	, EMAIL1
	, EMAIL2
) VALUES (
	#{mbId}
	, #{pw}
	, #{nm}
	, #{post}
	, #{addr1}
	, #{addr2}
	, #{tel1}
	, #{tel2}
	, #{tel3}
	, #{email1}
	, #{email2}
)

mapper.update:
UPDATE
		TB_MEMBER
SET
	PW = #{pw}
	, NM = #{nm}
	, POST = #{post}
	, ADDR1 = #{addr1}
	, ADDR2 = #{addr2}
	, TEL1 = #{tel1}
	, TEL2 = #{tel2}
	, TEL3 = #{tel3}
	, EMAIL1 = #{email1}
	, EMAIL2 = #{email2}
WHERE
	MB_ID = #{mbId}
