mapper.insert:
INSERT INTO
			TB_CART
			(
			CART_CD
			,IT_CD
			,MB_ID
			,AMOUNT
			,OPT_CD
			)
VALUES
			(
			TB_CART_SEQ.NEXTVAL
			,#{itCd}
			,#{mbId}
			,#{amount}
			,#{optCd}
			)

mapper.update:
UPDATE TB_CART
SET
	AMOUNT = #{amount}
WHERE
	MB_ID = #{mbId}
AND
	CART_CD = #{cartCd}

mapper.insert:

	SELECT
			TB_ORDER_INFO_SEQ.NEXTVAL
	FROM
			DUAL


INSERT INTO
TB_ORDER_INFO
(
		ORD_CD
		,DLVY_CD
		,ORD_NM
		,ORD_POST
		,ORD_ADDR1
		,ORD_ADDR2
		,ORD_TEL
		,PAY_METH
)
VALUES
(
		#{ordCd}
		,#{dlvyCd}
		,#{ordNm}
		,#{ordPost}
		,#{ordAddr1}
		,#{ordAddr2}
		,#{ordTel}
		,#{payMeth}
)

mapper.update:
UPDATE TB_DELIVERY_INFO SET TB_DELIVERY_INFO.DLVY_END =
(SELECT DLVY_END FROM ( SELECT TB_UP_TM.DLVY_END FROM TB_UP_TM WHERE TB_UP_TM.DLVY_CD=#{dlvyCd} )
WHERE TB_DELIVERY_INFO.DLVY_CD=#{dlvyCd};
